state.vs_src:
varying float alpha;

void main(void) {
    vec4 tp = m_mat * vec4(v_pos, 1.0);

    tp = v_mat * tp;
    gl_Position = p_mat * tp;
    gl_PointSize = 275.0 / pow(gl_Position.z, 2.0);
    f_col = d_col;
    alpha = (v_pos.z - 0.25);
}

state.ps_src:
varying float alpha;

void main(void)
{
    vec4 fc = texture2D(diffuse, gl_PointCoord) * f_col;

    fc.rgb += a_col.rgb;

    if(fc.a < 0.5)
        discard;

    gl_FragColor = vec4(fc.rgb, alpha);
}
