void main(void) {
    mat4 mv_mat = v_mat * m_mat;
    vec4 tp = mv_mat * vec4(v_pos, 1.0);

    view_pos = vec3(tp);
    gl_Position = p_mat * tp;
    f_norm = normalize(n_mat * v_norm);
    f_uv0 = v_uv0;
    f_col = d_col;
}

state.ps_src:
void main(void) {
    vec4 fc = vec4(0.0, 0.0, 0.0, f_col.a);
    vec3 n_dir = normalize(f_norm * -(texture2D(n_tex, f_uv0.st).rgb - 0.5 * 2.0));
    vec3 v_dir = normalize(-view_pos);
    vec3 l0_c_dir = normalize(l0_pos - view_pos);
    float l0_dd = max(dot(n_dir, l0_c_dir), 0.0);
    float spec_fac = pow(max(0.0, dot(reflect(-l0_c_dir, n_dir), v_dir)), 1.0 / shinyness);

    fc.rgb += l0_d_col.rgb * f_col.rgb * l0_dd * l0_power;

    fc.rgb += l0_power * l0_s_col.rgb * s_col.rgb * spec_fac * texture2D(s_tex, f_uv0.st).rgb;

    fc *= texture2D(d_tex, f_uv0.st);
    fc.rgb += texture2D(e_tex, f_uv0.st).rgb;
    fc.rgb += a_col.rgb;

    gl_FragColor = fc;
}
