    vec3 n_dir = normalize(f_norm);
    vec3 l0_v2l_n = normalize(l0_v2l);
    float l0_dd = max(0.0, dot(n_dir, l0_v2l_n));
    float l0_spec_fac = pow(max(0.0, dot(reflect(-l0_v2l_n, n_dir), eye_pos)), shinyness + 1.0);

    fc.rgb += l0_d_col.rgb * l0_dd * l0_power;
    fc.rgb += shinyness * l0_power * l0_s_col.rgb * s_col.rgb * l0_spec_fac;

    fc.rgb *= f_col.rgb;
    fc *= (texture2D(d_tex, vec2(f_uv0.x*texture_scale,(f_uv0.y+time)*texture_scale))*texture_alpha)+1.-texture_alpha;
    fc.rgb += a_col.rgb;



    //layer fog    

    float layer_fog = (z_offset/displacement_scale+displacement_scale/2.)*layer_fog_top;
    layer_fog = (1.-clamp(layer_fog,0.,1.))*layer_fog_amount;
    layer_fog = clamp(abs(layer_fog),0.,1.);
    layer_fog=(clamp(cnoise(vec3(vertex_pos.x,vertex_pos.y+time,vertex_pos.z)*2.)*2.,0.,1.)*layer_fog*0.5)+layer_fog*0.5;
    fc=mix(fc,layer_fog_color,layer_fog);

    //distance fog
    float m = 0.0;
    float world_w = 1000.;
    float fog_range = abs(fog_far-fog_near);
    float fz = gl_FragCoord.z/world_w*fog_range;
    float fw = gl_FragCoord.w/world_w*fog_range;
    m = (fz/fw)/fog_range-abs(fog_near/fog_range);


    float d = clamp(m, 0.0, 1.00);    


    //occlusion
    fc.rgb *= 1.-texture2D(displacement_texture, vec2(f_uv0.x,f_uv0.y+time)).r;
    fc.a = 1.0 - d;

    //fc = mix(fc,texture2D(background,vec2(gl_FragCoord.x/viewport_width,gl_FragCoord.y/viewport_height) * 0.5),d);
