void main(void) {
    mat4 mv_mat = v_mat * m_mat;
    vec4 tp = mv_mat * vec4(v_pos, 1.0);

    gl_Position = p_mat * tp;
    f_norm = normalize(n_mat * v_norm);
    f_uv0 = v_uv0;
    f_col = d_col;
}

state.ps_src:
void main(void) {
    vec4 fc = vec4(0.0, 0.0, 0.0, f_col.a);
    fc.rgb += f_col.rgb;
    fc *= texture2D(d_tex, f_uv0.st) * texture2D(blend_tex, f_uv0.st);
    fc.rgb += a_col.rgb;

    gl_FragColor = fc;
}
