state.vs_src:
void main(void) {
    vec4 tp = m_mat * vec4(v_pos, 1.0);

    tp = v_mat * tp;
    gl_Position = p_mat * tp;
    f_col = d_col;
    f_norm = n_mat * v_norm;
    f_uv0 = v_uv0;
}

state.ps_src:
void main(void) {
    vec4 fc = vec4(0.0, 0.0, 0.0, f_col.a);
    float rt = time * 0.001;

    fc.rgb = f_col.rgb;
    fc *= texture2D(d_tex, f_uv0.st);
    fc.rgb += a_col.rgb;

    float luma_1 = texture2D(noise, (f_uv0.st - vec2(rt * 0.73, rt * 0.91)) * 75.0).r;
    float luma_2 = texture2D(noise, (f_uv0.st - vec2(rt * 0.83, rt * -0.67)) * 200.0).r;
    float mix = pow((luma_1 + luma_2) * 0.5, 3.0);

    fc.rgb += vec3(mix, mix, mix);

    gl_FragColor = fc;
}
