state.vs_src:
void main(void) {
    view_pos = (v_mat * m_mat * vec4(v_pos, 1.0)).xyz;
    gl_Position = p_mat * v_mat * m_mat * vec4(v_pos, 1.0);
    f_norm = normalize((n_mat * vec4(v_norm, 0.0)).xyz);
    f_uv0 = v_uv0;
    f_col = d_col;
}

state.ps_src:
void main(void) {
    vec4 fc = vec4(a_col.rgb, f_col.a);
    vec3 n_dir = normalize(f_norm);
    vec3 v_dir = -normalize(view_pos);
    vec3 l0_dir = normalize(l0_pos);
    float l0_dd = dot(n_dir, l0_dir);
    fc += l0_d_col * f_col * max(0.0, l0_dd) * l0_power;
    if(l0_dd >= 0.0)
        fc += l0_power * vec4(vec3(l0_s_col) * s_col.rgb * pow(max(0.0, dot(-reflect(l0_dir, n_dir), v_dir)), 1.0 / (1.0 + shinyness)), 0.0);
    fc = fc * texture2D(tex0, f_uv0.st);
    if(e2_alpha_clip > 0 && fc.a < 0.5) discard;
    gl_FragColor = fc;
}
