void main(void) {
    mat4 mv_mat = v_mat * m_mat;
    vec4 tp = mv_mat * vec4(v_pos, 1.0);

    gl_Position = p_mat * tp;
    f_norm = normalize(n_mat * v_norm);
    f_uv0 = v_uv0;
    f_col = d_col;
}

state.ps_src:
void main(void) {
    vec4 fc = vec4(0.0, 0.0, 0.0, f_col.a);
    vec4 td = texture2D(d_tex, f_uv0.st + (vec2(cos(time*3.45), sin(time*4.563)) * 0.05));
    float po = sin(time * 13.97)/(128.0 * cos(time*47.53));
    vec3 grey = vec3(0.299, 0.587, 0.114);

    float d = abs(dot(texture2D(d_tex, f_uv0.st + vec2(0.0, -po)).rgb, grey) - dot(texture2D(d_tex, f_uv0.st + vec2(0.0, po)).rgb, grey));

    d += abs(dot(texture2D(d_tex, f_uv0.st + vec2(0.0, -po)).rgb, grey) - dot(texture2D(d_tex, f_uv0.st + vec2(0.0, po)).rgb, grey));
    d *= 10.0;

    fc.rgb += f_col.rgb;
    fc *= td;
    fc.rgb += a_col.rgb;

    gl_FragColor = vec4(fc.rgb+vec3(d, d * 0.5, d * 0.2), 1.0);
}
