uniform sampler2D diffuse_tex;

void main(void)
{
        vec3 pos = vec3(tan(v_pos.x * mul1 + time), cos(v_pos.x * mul2 + time), cos(v_pos.x * mul3 + time));
	gl_Position = p_mat * v_mat * m_mat * vec4(pos, 1.0);

	gl_PointSize = size;
        f_col = d_col;
}

state.ps_src:
void main(void)
{
        vec4 t = texture2D(diffuse_tex, gl_PointCoord);
        vec4 c = t * f_col;

        if(c.a < 0.5) discard;

        gl_FragColor = c;
}
