state.vs_src:
void main(void) {
    mat4 mv_mat = v_mat * m_mat;

    vec3 v = v_pos;
    vec3 n = v_norm;

    v.x += cos((v_pos.x * scalar_x)+time) * 0.5 + 0.75;
    v.y += cos((v_pos.y * scalar_y)+time) * 0.5 + 0.75;
    v.z += cos((v_pos.z * scalar_z)+time) * 0.5 + 0.75;

    n.x += cos((v_norm.x * scalar_x)+time) * 0.5 + 0.75;
    n.y += cos((v_norm.y * scalar_y)+time) * 0.5 + 0.75;
    n.z += cos((v_norm.z * scalar_z)+time) * 0.5 + 0.75;


    vec4 tp = mv_mat * vec4(v, 1.0);

    view_pos = vec3(tp);
    gl_Position = p_mat * tp;
    f_norm = normalize(n_mat * n);
    f_uv0 = v_uv0;
    f_col = d_col;
}

state.ps_src:
void main(void) {
    vec4 fc = vec4(0.0, 0.0, 0.0, f_col.a);
    vec3 n_dir = normalize(f_norm);
    vec3 v_dir = normalize(-view_pos);
    vec3 l0_c_dir = normalize(l0_pos - view_pos);
    float l0_dd = max(dot(n_dir, l0_c_dir), 0.0);
    float spec_fac = pow(max(0.0, dot(reflect(-l0_c_dir, n_dir), v_dir)), 1.0 / shinyness);

    fc.rgb += l0_d_col.rgb * f_col.rgb * l0_dd * l0_power;

    fc.rgb += l0_power * l0_s_col.rgb * s_col.rgb * spec_fac;

    fc *= texture2D(d_tex, f_uv0.st);
    fc.rgb += a_col.rgb;

    gl_FragColor = fc;
}
